  const [hoveredAction, setHoveredAction] = useState<GAMELOOP_ACTION | ''>('');

  const theme = useTheme();
  const matches = useMediaQuery('(max-width:600px)');
  const { showModal } = useGlobalModalContext();
  const router = useRouter();

  const color = useMemo(() => theme.palette.text.secondary, [theme]);
  const activeColor = useMemo(() => theme.palette.primary.main, [theme]);

